mapper.insert:
  		INSERT INTO free_board (                         
			      bo_no       
			    , bo_title 
			    , bo_category 
			    , bo_writer   
			    , bo_pass   
			    , bo_content  
			    , bo_ip       
			    , bo_hit    
			    , bo_reg_date 
			    , bo_mod_date 
			    , bo_del_yn  
			    ) 			

			 VALUES (                                   
				SEQ_FREE_BOARD.NEXTVAL
				, #{boTitle}
				, #{boCategory}	
				, #{boWriter}				   
				, #{boPass}
				, #{boContent}
				, #{boIp} 				   
				, #{boHit} 
				, sysdate	
				, null 			   
				, 'N' 

				)

mapper.update:

UPDATE free_board     
   SET bo_title= #{boTitle}
     , bo_writer = #{boWriter}
     , bo_category = #{boCategory}
     , bo_content = #{boContent}
     , bo_mod_date = SYSDATE  			
 WHERE bo_no = #{boNo}

mapper.delete:
    UPDATE free_board       
	   SET bo_del_yn = 'Y'  
	 WHERE bo_no    = #{boNo}

mapper.update:
    UPDATE free_board       
	   SET bo_hit = bo_hit + 1
	 WHERE bo_no = #{boNo}
